export interface ColorInterface {
    alpha: number;
    hex: string;
    rgb: number[];
    rgba: number[];
    hsv: number[];
}
export default class Color implements ColorInterface {
    static collection: import("../types").ColorCollection;
    alpha: number;
    private color;
    constructor(color?: string);
    clone(): Color;
    rgb: number[];
    rgba: number[];
    hsv: number[];
    hex: string;
    brighterThan(color: ColorInterface, difference: number): this;
    darkerThan(color: ColorInterface, difference: number): this;
    brighterOrDarkerThan(color: ColorInterface, difference: number): this;
    private rgbToHex;
    private hexToRgb;
    private rgbToHsv;
    private hsvToRgb;
}
